import { settings } from '../assets/configs/game'
import './Reset.css'

export const Reset = ({ onClick, userSelection, userScore, pcScore }) => {
    return (
        userSelection !== '' && (
            <div onClick={onClick} className='reset-btn'>
                <h3>
                    {userScore === settings.winTarget || pcScore === settings.winTarget
                        ? 'Play again'
                        : 'Reset'}
                </h3>
            </div>
        )
    )
}
